import axios from 'axios';

const apiKey = '8a20b32ade4d94ec689e556b053961a3';

const apiClient = axios.create({
  baseURL: 'https://api.themoviedb.org/3',
  params: {
    api_key: apiKey,
  },
});

export default apiClient;
